// Set new default font family and font color to mimic Bootstrap's default styling
Chart.defaults.global.defaultFontFamily = 'Nunito', '-apple-system,system-ui,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif';
Chart.defaults.global.defaultFontColor = '#858796';

// Pie Chart Example
var ctx = document.getElementById("myPieChart");

fetch('http://localhost:3000/admin/data')
  .then(res => res.json())
  .then(data => {
    const suratMasuk = data.data.suratMasuk;
    const disposisi = data.data.disposisi;
    const suratKeluar = data.data.suratKeluar;
    
    var myPieChart =  new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: ["Surat Masuk", "Disposisi","Surat Keluar"],
        datasets: [{
          data: [suratMasuk, disposisi, suratKeluar],
          backgroundColor: ['#4e73df', '#36b9cc', '#ffc107'],
          hoverBackgroundColor: ['#2e59d9', '#2c9faf', '#ffc107'],
          hoverBorderColor: "rgba(234, 236, 244, 1)",
        }],
      },
      options: {
        maintainAspectRatio: false,
        tooltips: {
          backgroundColor: "rgb(255,255,255)",
          bodyFontColor: "#858796",
          borderColor: '#dddfeb',
          borderWidth: 1,
          xPadding: 15,
          yPadding: 15,
          displayColors: false,
          caretPadding: 10,
        },
        legend: {
          display: false
        },
        cutoutPercentage: 80,
      },
    });
  })
  .catch(err => console.log(err))



